import React, { Component } from 'react';
import Header from './components/header/index';
import Images from './components/images';
import './App.css';
import avengers from './avengers.json';
import Wrapper from './components/wrapper';

class App extends Component {

  state = {
    avengers,
    score: 0,
    highScore: 0,
    guesses: [],
    message: ""
  }

  makeImagesRandom = () => {
    let randomArray = [];
    randomArray = this.state.avengers.sort(() => 1.0 - Math.random());
    this.setState({ avengers: randomArray });
  };

  imageClick = id => {
    if (this.state.guesses.includes(id)) {
      this.setState({
        guesses: [],
        score: 0,
        message: "Sorry, that was incorrect!"
      });
    } else {
      this.setState({
        guesses: this.state.guesses.concat(id),
        score: this.state.score + 1,
        message: "Correct.  I love you 3000!"
      });
    };
    this.makeImagesRandom();
  };

  render() {
    return (
      <Wrapper>
        <Header 
          score={this.state.score}
          message={this.state.message}
        />
        <div id="image-area">
          {this.state.avengers.map(avenger => <Images {...avenger} imageClick={this.imageClick} />)}
        </div>
      </Wrapper>
    );
  }

}

export default App;
