import React, { Component } from 'react';
import Header from './components/header/index';
import Images from './components/images';
import './App.css';
import avengers from './avengers.json';
import Wrapper from './components/wrapper';

class App extends Component {

  state = {
    avengers,
    score: 0,
    highScore: 0,
    guesses: []
  }

  makeImagesRandom = () => {
    let randomArray = [];
    randomArray = this.state.avengers.sort(() => 0.5 - Math.random());
    this.setState({ avengers: randomArray });
  };

  imageClick = id => {
    if (this.state.guesses.includes(id)) {
      this.setState({
        guesses: [],
        score: 0
      });
    } else {
      this.setState({
        guesses: this.state.guesses.concat(id),
        score: this.state.score + 1
      });
    };
    this.makeImagesRandom();
  };

  render() {
    return (
      <Wrapper>
        <Header />
        <div id="image-area">
          {this.state.avengers.map(avenger => <Images {...avenger} />)}
        </div>
      </Wrapper>
    );
  }

}

export default App;
